import React, { useContext } from 'react';
import {
  Wrapper,
  Container,
  LogoWrapper,
  Heading,
  SubHeading,
  Input,
  Button,
  LinkButton,
  Offer,
} from './authentication-form.style';
import { FormattedMessage, useIntl } from 'react-intl';
import { AuthContext } from 'contexts/auth/auth.context';

export default function ForgotPasswordModal() {
  const { authDispatch } = useContext<any>(AuthContext);
  const intl = useIntl();
  const toggleSignInForm = () => {
    authDispatch({
      type: 'SIGNIN',
    });
  };
  return (
    <Wrapper>
      <Container style={{ paddingBottom: 30 }}>
        <Heading>
          <FormattedMessage
            id="forgotPassText"
            defaultMessage="Forgot Password"
          />
        </Heading>

        <SubHeading>
          <FormattedMessage
            id="sendResetPassText"
            defaultMessage="We'll send you a link to reset your password"
          />
        </SubHeading>

        <Input
          type="text"
          placeholder={intl.formatMessage({
            id: 'emailAddressPlaceholder',
            defaultMessage: 'Email Address or Contact No.',
          })}
        />

        <Button
          variant="primary"
          size="big"
          style={{ width: '100%' }}
          type="submit"
        >
          <FormattedMessage
            id="resetPasswordBtn"
            defaultMessage="Reset Password"
          />
        </Button>
        <Offer style={{ padding: '20px 0 0' }}>
          <FormattedMessage id="backToSign" defaultMessage="Back to" />{' '}
          <LinkButton onClick={toggleSignInForm}>
            <FormattedMessage id="loginBtnText" defaultMessage="Login" />
          </LinkButton>
        </Offer>
      </Container>
    </Wrapper>
  );
}
