import React from 'react';
import { themeGet } from '@styled-system/theme-get';
import Carousel from 'react-multi-carousel';
import styled from 'styled-components';
import { TransformWrapper, TransformComponent } from "react-zoom-pan-pinch";
import ReactImageMagnify from 'react-image-magnify';

const SingleItem = styled.li`
  border: 1px solid ${themeGet('colors.borderColor', '#f1f1f1')};
  border-radius: 6px;
  margin-right: 20px;
  padding: 0;
  outline: none;
  width: 70px;
  height: auto;
  overflow: hidden;

  &:last-child {
    margin-right: 0;
  }

  &.custom-dot--active {
    border: 2px solid ${themeGet('colors.primary', '#009E7F')};
  }
`;
const responsive = {
  desktop: {
    breakpoint: {
      max: 3000,
      min: 1024,
    },
    media: 1,
  },
  mobile: {
    breakpoint: {
      max: 464,
      min: 0,
    },
    media: 1,
  },
  tablet: {
    breakpoint: {
      max: 1024,
      min: 200,
    },
    media: 1,
  },
};

const CarouselWithCustomDots = ({
  media,
  deviceType: { mobile, tablet, desktop },
  title,
  ...rest
}: any) => {
  const getAbsMedia = (url)=> url
  console.log(media, "pullu")
  const children = (media || []).slice(0,6).map((item: any, index: number) => (
    <div key={index}>
      {/*<img src={getAbsMedia(item.URLs.large)}/>*/}
      {/*{console.log(Math.ceil(item.full_height * (450 / item.full_width)))}*/}
      {desktop ? <ReactImageMagnify key={index} {...{
        smallImage: {
          alt: title,
          isFluidWidth: false,
          src: getAbsMedia(item.URLs.large),
          width: Math.ceil(item.width * (450 / item.height)),
          height: 450,
          ClassName: 'product-image',
          style: {
            minWidth: 'auto',
            height: 'auto',
            position: 'relative',
            margin: 'auto',
          }
        },
        imageClassName: 'product-image',
        imageStyle: {
          minWidth: 'auto',
          height: 'auto',
          position: 'relative',
          margin: 'auto',
        },
        largeImage: {
          src: getAbsMedia(item.URLs.large),
          width: item.width,
          height: item.height,
        },
        lensStyle: {
          backgroundColor: 'rgba(78,78,78,.3)',
          backdropFilter: 'blur(3px)',
          borderRadius: '0px',
          caretColor: '#009E7F'
        },
        enlargedImageContainerStyle: {
          borderRadius: '0px',
        },
        enlargedImagePortalId: 'product-info-786',
        isHintEnabled: true,
      }} style={{
        position: 'absolute',
        marginLeft: 'auto',
        marginRight: 'auto'
      }}/> :
        <TransformWrapper>
          <TransformComponent>
          <img
            src={getAbsMedia(item.URLs.large)}
            key={index}
            alt={title}
            style={{
              minWidth: 'auto',
              height: 'auto',
              position: 'relative',
              margin: 'auto',
            }}
            className="product-image"
          />
        </TransformComponent>
      </TransformWrapper>
      }
      </div>
  ));
  const images = (media || []).map((item: any, index: number) => (
    <img
      src={ getAbsMedia(item.URLs.small)}
      key={index}
      alt={title}
      style={{ width: '100%', height: '100%', position: 'relative' }}
    />
  ));
  const CustomDot = ({
    index,
    onClick,
    active,
    carouselState: { currentSlide, deviceType },
  }: any) => {
    return (
      <SingleItem
        data-index={index}
        key={index}
        onClick={onClick}
        className={`custom-dot ${active && 'custom-dot--active'}`}
      >
        {React.Children.toArray(images)[index]}
      </SingleItem>
    );
  };
  let deviceType = 'desktop';
  if (mobile) {
    deviceType = 'mobile';
  }
  if (tablet) {
    deviceType = 'tablet';
  }
  return (
    <Carousel
      showDots
      ssr
      infinite={true}
      slidesToSlide={1}
      containerClass="carousel-with-custom-dots"
      responsive={responsive}
      deviceType={deviceType}
      autoPlay={false}
      arrows={true}
      customDot={<CustomDot />}
      {...rest}
    >
      {children}
    </Carousel>
  );
};

export default CarouselWithCustomDots;
