import React from 'react';
import ContentLoader from 'react-content-loader';

const PostLoader = (props) => (
  <ContentLoader
    height={350}
    width={245}
    speed={2}
    backgroundColor="#f3f3f3"
    foregroundColor="#ecebeb"
    {...props}
  >
    <rect x="2" y="2" rx="0" ry="0" width="240" height="197" />
